public DataGridModel<$expr$> getDataGridData() {
        // 获得表格对象
        if (model == null) {
            model = new DataGridModel<$expr$>()
            {

                @Override
                public List<$expr$> fetchData(int first, int pageSize, String sortField, String sortOrder) {
                    // 获取where条件Map集合
                    Map<String, Object> conditionMap = ListGenerator.getSearchMap(getRequestContext().getComponents(),
                            sortField, sortOrder);
                    PageData<$expr$> pageData = service.paginatorList(conditionMap, first, pageSize);
                    this.setRowCount(pageData.getRowCount());
                    return pageData.getList();
                }

            };
        }
        return model;
    }
